import Layout from "../components/layout";
import SectionHead from "../components/sectionhead";

import { useEffect, useState } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";

export default function Calendar() {
  const [data, setData] = useState({ events: [] });

  useEffect(async () => {
    var cal_id = "pmtthq7beefrulnrjbd9nj9e5s@group.calendar.google.com";
    var api_key = "AIzaSyDCdsXt9s_kxJp3SUjzzHng1Flgbao7jpc";

    function formatTime(date) {
      var hours = date.getHours();
      var minutes = date.getMinutes();
      var ampm = hours >= 12 ? "pm" : "am";
      hours = hours % 12;
      hours = hours ? hours : 12;
      minutes = minutes < 10 ? "0" + minutes : minutes;
      var strTime = hours + ":" + minutes + " " + ampm;
      return strTime;
    }

    const result = await axios({
      method: "get",
      url:
        "https://www.googleapis.com/calendar/v3/calendars/" +
        cal_id +
        "/events" +
        "?key=" +
        api_key,
      data: {
        timeMin: dateNow.toISOString(),
        timeMax: new Date(
          dateNow.setMonth(dateNow.getMonth() + 1)
        ).toISOString(),
        orderBy: "startTime",
        singleEvents: true,
      },
    }).then((response) => {
      var events = response.items;
      events.forEach((e) => {
        console.log(e);
        var startTime = e.start.dateTime
          ? new Date(e.start.dateTime)
          : new Date(e.start.date);
        var endTime = e.end.dateTime
          ? new Date(e.end.dateTime)
          : new Date(e.end.date);
        var startDate = startTime.toDateString();
        var endDate = endTime.toDateString();
        var startTimeString =
          startTime && startTime.getHours() ? formatTime(startTime) : "";
        var endTimeString =
          endTime && endTime.getHours() ? formatTime(endTime) : "";
        var timeString = endTimeString
          ? `${startTimeString} - ${endTimeString}`
          : startTimeString;

        var dateString =
          startDate == endDate
            ? `${startDate}, ${timeString}`
            : `${startDate}, ${startTimeString} - ${endDate}, ${endTimeString}`;
        var locationString = e.location ? `${e.location}` : "";
        return {
          htmlLink: e.htmlLink,
          summary: e.summary,
          locationString: locationString,
          dateString: dateString,
        };
      });
    });

    document.addEventListener("DOMContentLoaded", function (event) {
      EVENTS_DIV = document.getElementById("events");
      var dateNow = new Date();
      $.ajax({
        url:
          "https://www.googleapis.com/calendar/v3/calendars/" +
          cal_id +
          "/events" +
          "?key=" +
          api_key,
        data: {
          timeMin: dateNow.toISOString(),
          timeMax: new Date(
            dateNow.setMonth(dateNow.getMonth() + 1)
          ).toISOString(),
          orderBy: "startTime",
          singleEvents: true,
        },
        success: function (data) {
          var events = data.items;
          events.forEach((e) => {
            console.log(e);
            var startTime = e.start.dateTime
              ? new Date(e.start.dateTime)
              : new Date(e.start.date);
            var endTime = e.end.dateTime
              ? new Date(e.end.dateTime)
              : new Date(e.end.date);
            var startDate = startTime.toDateString();
            var endDate = endTime.toDateString();
            var startTimeString =
              startTime && startTime.getHours() ? formatTime(startTime) : "";
            var endTimeString =
              endTime && endTime.getHours() ? formatTime(endTime) : "";
            var timeString = endTimeString
              ? `${startTimeString} - ${endTimeString}`
              : startTimeString;

            var dateString =
              startDate == endDate
                ? `${startDate}, ${timeString}`
                : `${startDate}, ${startTimeString} - ${endDate}, ${endTimeString}`;
            var locationString = e.location ? `${e.location}` : "";
            var card = `
      <a href="${e.htmlLink}" target="_blank"><div class="event-card">
        <h4>${e.summary}</h4>
        <h5>${locationString}</h5>
        <h6>${dateString}</h6>
      </div></a>
      `;
            EVENTS_DIV.innerHTML += card;
          });
        },
      });
    });
  });

  return (
    <Layout>
      <div className="content">
        <Container fluid className="section">
          <SectionHead title="Events_" top={true} />
          <div id="events"></div>
        </Container>
      </div>
    </Layout>
  );
}
