import styles from "../styles/Boardcard.module.css";

import { useEffect } from "react";
import Col from "react-bootstrap/Col";

export default function BoardCard({
  name,
  role,
  major,
  year,
  spiritAnimal,
  hobbies,
  quote,
  img,
}) {
  useEffect(() => {
    var imgCards = document.getElementsByClassName("img-card");
    for (var i = 0; i < imgCards.length; i++) {
      imgCards[i].addEventListener("click", function () {
        if (this.classList.contains("active")) {
          this.classList.remove("active");
        } else {
          this.classList.add("active");
        }
      });
    }
  }, []);
  return (
    <Col xl={3} lg={4} md={6} sm={12} className="text-center">
      <div className={styles["img-card"]}>
        <div className={styles["card-inner"]}>
          <div className={styles["flip-card"]}>
            <div className={styles["flip-text"]}>
              <h5>{major}</h5>
              <h6>{year}</h6>
              <div className={styles["flip-about"]}>
                <h4>About Me</h4>
                <h6>
                  <span className={styles["flip-category"]}>
                    Spirit Animal:
                  </span>
                  {spiritAnimal}
                </h6>
                <h6>
                  <span className={styles["flip-category"]}>Hobbies:</span>{" "}
                  {hobbies}
                </h6>
                <h6>
                  <span className={styles["flip-category"]}>
                    Favorite inspirational quote:
                  </span>{" "}
                  {quote}
                </h6>
              </div>
            </div>
          </div>
          <img className={styles["exec-pic"]} src={img} />
          <div className={styles["exec-text"]}>
            <h5 className={styles["exec-name"]}>{name}</h5>
            <h6 className={styles["exec-desc"]}>{role}</h6>
          </div>
        </div>
      </div>
    </Col>
  );
}
