import Layout from "../components/layout";
import SectionBody from "../components/sectionbody";
import SectionHead from "../components/sectionhead";
import { server } from "../utils/server";

// import axios from "axios";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";

export default function Register() {
  const handleSubmit = async (event) => {
    const base_url = "localhost:3003/api";

    const form = event.currentTarget;

    event.preventDefault();
    const formData = new FormData(form);

    const { success, message, data } = await server.postAsync(
      "/user",
      formData
    );
    console.log(message);

    if (success) {
      window.location.replace(data);
    }

    // const postUserRegistration = async () => {
    //   await axios
    //     .post(base_url + "/user", formData)
    //     .then((res) => {
    //       if (res.success) {
    //         window.location.replace(res.data);
    //       }
    //     })
    //     .catch((error) => {
    //       console.log(error);
    //     });
    // };

    // postUserRegistration();
  };
  return (
    <Layout>
      <div className="content">
        <Container className="section">
          <SectionHead title="Login_" top={true} />
          <SectionBody>
            <Form name="register-form" noValidate onSubmit={handleSubmit}>
              <Form.Group controlId="formEmailPass">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  required
                  type="email"
                  name="email"
                  placeholder="netID@illinois.edu"
                />
                <Form.Control.Feedback type="invalid">
                  Please enter your email.
                </Form.Control.Feedback>
                <Form.Label>Password</Form.Label>
                <Form.Control required type="password" name="password" />
                <Form.Control.Feedback type="invalid">
                  Please enter a password.
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="userInfo">
                <Form.Control
                  type="text"
                  name="firstName"
                  placeholder="First Name"
                />
                <Form.Control
                  type="text"
                  name="lastName"
                  placeholder="Last Name"
                />
                <Form.Control type="text" name="major" placeholder="Major" />
              </Form.Group>
              <Button type="submit">Submit</Button>
            </Form>
          </SectionBody>
        </Container>
      </div>
    </Layout>
  );
}
