import Layout from "../components/layout";
import SectionBody from "../components/sectionbody";
import SectionHead from "../components/sectionhead";
import styles from "../styles/Join.module.css";

import { useEffect, useState } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default function Join() {
  const [validated, setValidated] = useState(false);

  useEffect(() => {
    const url =
      "https://script.google.com/macros/s/AKfycbz1yXFCt7nFu-zpkEfLgMtQjXEMgN_aV_GXVbQ3-guQxRwmhAE/exec";

    const form = document.forms["join-form"];

    form.addEventListener("submit", (e) => {
      e.preventDefault();
      if (form.checkValidity() === false) {
        e.preventDefault();
        e.stopPropagation();
      } else {
        setValidated(true);
      }
      const formData = new FormData(form);
      const inputs = form.querySelectorAll("input");
      inputs.forEach((input) => (input.disabled = true));

      const postJoinRequest = async () => {
        const result = await axios
          .post(url, formData)
          .then((response) => {
            alert("Your netID was recorded. Thanks!");
          })
          .catch((error) => {
            alert("Error: " + error.message);
          })
          .finally(inputs.forEach((input) => (input.disabled = false)));
      };

      postJoinRequest();
    });
  });
  return (
    <Layout>
      <div className="content">
        <Container fluid className="section">
          <SectionHead title="Join WECE_" top={true} />
          <SectionBody>
            <p>
              Sign up here to get weekly newsletters with general meetings,
              upcoming events, and more.
            </p>
            <Form name="join-form" id="join-form" className="form" noValidate>
              <Form.Group controlId="formNetId">
                <Form.Label>NetId</Form.Label>
                <Form.Control
                  type="text"
                  name="netID"
                  placeholder="Enter NetId"
                  required
                />
                <Form.Control.Feedback type="invalid">
                  Please enter your NetID.
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="formCommitteeInput">
                <Form.Label>
                  Check the committees you are interested in:
                </Form.Label>
                <Form.Check
                  type="checkbox"
                  name="academic"
                  value="academic"
                  label="Academic"
                />
                <Form.Check
                  type="checkbox"
                  name="infrastructure"
                  value="infrastructure"
                  label="Infrastructure"
                />
                <Form.Check
                  type="checkbox"
                  name="mentorship"
                  value="mentorship"
                  label="Mentorship"
                />
                <Form.Check
                  type="checkbox"
                  name="outreach"
                  value="outreach"
                  label="Outreach"
                />
                <Form.Check
                  type="checkbox"
                  name="marketing"
                  value="marketing"
                  label="Marketing"
                />
                <Form.Check
                  type="checkbox"
                  name="social"
                  value="social"
                  label="Social"
                />
                <Form.Check
                  type="checkbox"
                  name="technical"
                  value="technical"
                  label="Technical"
                />
              </Form.Group>
              <Button id={styles["submit-form"]} type="submit">
                Submit
              </Button>
              {/* <h6>
                <span className="category">NetID:</span>
                <input type="text" name="netID" id="netID" required />
              </h6>
              <h6 id="checkbox-header">Committees I'm interested in:</h6>
              <div className="committee-checkboxes">
                <input type="checkbox" name="academic" value="academic" />
                Academic
                <br />
                <input
                  type="checkbox"
                  name="infrastructure"
                  value="infrastructure"
                />
                Infrastructure
                <br />
                <input type="checkbox" name="mentorship" value="mentorship" />
                Mentorship
                <br />
                <input type="checkbox" name="outreach" value="outreach" />
                Outreach
                <br />
                <input type="checkbox" name="marketing" value="marketing" />
                Marketing
                <br />
                <input type="checkbox" name="social" value="social" /> Social
                <br />
                <input type="checkbox" name="technical" value="technical" />
                Technical
                <br />
              </div>
              <br /> */}
              {/* <div className="button-join">
                <button
                  type="submit"
                  id={styles["submit-form"]}
                  className="btn"
                >
                  Join
                </button>
              </div> */}
            </Form>
          </SectionBody>
        </Container>
      </div>
    </Layout>
  );
}
